Panel refSeqPanel = otherPanel.getSequencePanel(2);		refSeqCanvas = refSeqPanel.getCanvas();		mySeq = (MatrixSequence)mySeqPanel.getSequence();		mySeqCanvas = mySeqPanel.getCanvas();		refSeq = (MatrixSequence)refSeqPanel.getSequence();		//setBackground(Color.blue);	}	MesquitePopup popup=null;	public void mouseDown (int modifiers, int clickCount, long when, int x, int y, MesquiteTool tool) {		if (MesquiteEvent.rightClick(modifiers) || MesquiteEvent.controlKeyDown(modifiers)){			if (popup==null) {				popup = new MesquitePopup(this);				popup.add(new MesquiteMenuItem("Align", parent.window.getOwnerModule(), new MesquiteCommand("align", this)));				add(popup);			}			popup.show(this, x,y);		}	}	public Object doCommand(String commandName, String arguments, CommandChecker checker) {		if (checker.compare(this.getClass(), "Tries to align this with base contig", null, commandName, "align")) {			alignToRef();		}		else			return  super.doCommand(commandName, arguments, checker);		return null;	}	void alignToRef(){		int center = mySeqCanvas.getLocalIndexFromUniversalBase(parent.getCenterBase());		int otherCenter = refSeqCanvas.getLocalIndexFromUniversalBase(otherPanel.getCenterBase());		int centerAsViewed = center;		int otherCenterAsViewed = otherCenter;		//first, align sequences		long[] mySequence = mySeq.getSequenceAsLongs();		long[] otherSequence = refSeq.getSequenceAsLongs();		int[][] subs = AlignUtil.getDefaultSubstitutionCosts(DNAState.maxDNAState +1); 		MesquiteInteger gapOpen = new MesquiteInteger();		MesquiteInteger gapExtend = new MesquiteInteger();		MesquiteInteger gapOpenTerminal = new MesquiteInteger();		MesquiteInteger gapExtendTerminal = new MesquiteInteger();		AlignUtil.getDefaultGapCosts(gapOpen, gapExtend, gapOpenTerminal, gapExtendTerminal); 		gapOpenTerminal.setValue(1);		gapExtendTerminal.setValue(1);		PairwiseAligner aligner = new PairwiseAligner(true,  subs, gapOpen.getValue(), gapExtend.getValue(), gapOpenTerminal.getValue(), gapExtendTerminal.getValue(), 4);		aligner.setMaintainOrder(true);		MesquiteNumber score = new MesquiteNumber();		if (mySeqCanvas.isShownReversed()) {			reverse(mySequence);			centerAsViewed = mySequence.length-center-1;		}		if (mySeqCanvas.isShownComplemented())			complement(mySequence);		if (refSeqCanvas.isShownReversed()){			otherCenterAsViewed = otherSequence.length-otherCenter-1;			reverse(otherSequence);		}		if (refSeqCanvas.isShownComplemented())			complement(otherSequence);		long[][] results = aligner.alignSequences( mySequence, otherSequence, true, score);		//now, find region of overlap between aligned sequences		int firstMy = -1; 		int lastMy = -1;		int firstOther = -1; 		int lastOther = -1;		for (int ic = 0; ic<results.length; ic++){			if (DNAState.isCombinable(results[ic][0])) {				if (firstMy < 0)					firstMy = ic;				lastMy = ic;			}			if (DNAState.isCombinable(results[ic][1])) {				if (firstOther < 0)					firstOther = ic;				lastOther = ic;			}		}		int firstOverlap = -1;		int lastOverlap = -1;		if (firstMy<firstOther){ //my starts to left			if (lastMy < firstOther) //no overlap				;			else if (lastOther<= lastMy) { //other contained in my				firstOverlap = firstOther;				lastOverlap = lastOther;			}			else {  //partial overlap				firstOverlap = firstOther;				lastOverlap = lastMy;			}		}		else {  //other starts to left			if (lastMy < firstOther) //no overlap				;			else if (lastMy<= lastOther) { //my contained in other				firstOverlap = firstMy;				lastOverlap = lastMy;			}			else {  //partial overlap				firstOverlap = firstMy;				lastOverlap = lastOther;			}		}		//OK, there is overlap, so adjust centering		if (firstOverlap >=0){  //there is overlap, so adjust centering for alignment			//first find if reference sequence is already shown in region of overlap			int countCenterOther = 0;			int icAtCurrentCenter = -1;			for (int ic = 0; ic<results.length && icAtCurrentCenter < 0; ic++){				if (!DNAState.isInapplicable(results[ic][1])){					if (countCenterOther == otherCenterAsViewed)						icAtCurrentCenter = ic;					countCenterOther++;				}			}			if (icAtCurrentCenter >= firstOverlap && icAtCurrentCenter <= lastOverlap){				//now center my at icAtCurrentCenter				int countMy = 0;				for (int ic = 0; ic<icAtCurrentCenter; ic++){					if (!DNAState.isInapplicable(results[ic][0]))						countMy++;				}				int centerMyAt = countMy;				if (mySeqCanvas.isShownReversed())					centerMyAt = mySequence.length-countMy-1;				parent.window.setSequencesLocked(false);				parent.scrollToMatrixBase(centerMyAt);				parent.window.setSequencesLocked(true);			}			else {  //no, current center of reference sequence is not in aligned overlap, so go to center of aligned overlap				int countMy = 0;				int countOther = 0;				int centerOfOverlap = (firstOverlap + lastOverlap)/2;				for (int ic = 0; ic<=centerOfOverlap; ic++){					if (!DNAState.isInapplicable(results[ic][0]))						countMy++;					if (!DNAState.isInapplicable(results[ic][1]))						countOther++;				}				int centerMyAt = countMy;				int centerOtherAt = countOther;				if (mySeqCanvas.isShownReversed())					centerMyAt = mySequence.length-countMy-1;				if (refSeqCanvas.isShownReversed())					centerOtherAt = otherSequence.length-countOther-1;				parent.window.setSequencesLocked(false);				parent.scrollToMatrixBase(centerMyAt);				otherPanel.scrollToMatrixBase(centerOtherAt);				parent.window.setSequencesLocked(true);			}		}	}	void reverse(long[] seq){		for (int i = 0; i<seq.length/2; i++){			long temp = seq[i];			seq[i] = seq[seq.length-1-i];			seq[seq.length-1-i] = temp;		}	}	void complement(long[] seq){		for (int i = 0; i<seq.length; i++)			seq[i] = DNAState.complement(seq[i]);	}	double compareStates(char char1, char char2){		long state = DNAState.fromCharStatic(char1);		long state2 = DNAState.fromCharStatic(char2);		int cardInt = CategoricalState.cardinality(state & state2);		int cardUn = CategoricalState.cardinality(state | state2);		if (cardUn == 0)			return 0;		return (cardInt*1.0/cardUn);	}	int distance = 8;	int widthHalf = 20;	public synchronized Color getBaseMatchColor(int ic){		int diff = otherCenter-center;		double s = 0;		if (parent.isShownReversed()){			ic = mySequence.length() - ic-1;		}		String acO = "";		String ac = "";		//looking at a window widthHalf left and right of this site		for (int ik = ic-widthHalf; ik<= ic + widthHalf; ik++){ //surveying window			//seeing that point in window is in both sequences			if (ik>=0 && ik<mySequence.length() && ik+diff>=0 && ik+diff<otherSequence.length()){				char state = mySequence.charAt(ik);				char otherState = otherSequence.charAt(ik+diff);				if (ic == center){					ac += state;					acO += otherState;				}				s += compareStates(state, otherState);			}		}		s = s/(2.0*widthHalf +1);		double bestScore = s;  //set this as baseline best match		int bestOffset = 0;		for (int offset= -distance; offset<=distance; offset++){			int icOffset = ic + offset;			if (icOffset>=0 && icOffset<mySequence.length()){				double sD = 0;				//looking at a window widthHalf left and right of this site				for (int ik = icOffset-widthHalf; ik<= icOffset + widthHalf; ik++){ //surveying window					//seeing that point in window is in both sequences					if (ik>=0 && ik<mySequence.length() && ik+diff-offset>=0 && ik+diff-offset<otherSequence.length()){						char state = mySequence.charAt(ik);						char otherState = otherSequence.charAt(ik+diff- offset);						sD += compareStates(state, otherState);					}				}				sD = sD/(2.0*widthHalf +1);				if (sD > bestScore) {					bestScore = sD;					bestOffset = offset;				}			}		}		//bestScore = bestScore*0.7;//to soften colors		if (bestOffset>0){			bestOffset = bestOffset/2;			if (bestOffset == 0)				bestOffset =1;			return MesquiteColorTable.getGreenScale(bestScore/bestOffset, 0, 1, false);		}		else if (bestOffset<0){			bestOffset = - bestOffset;			bestOffset = bestOffset/2;			if (bestOffset == 0)				bestOffset = 1;			return MesquiteColorTable.getRedScale(bestScore/bestOffset, 0, 1, false);		}		else {			if (ic>=0 && ic<mySequence.length() && ic+diff>=0 && ic+diff<otherSequence.length()){				char state = mySequence.charAt(ic);				char otherState = otherSequence.charAt(ic+diff);				if (state == otherState){					double score = compareStates(state, otherState);					if (score > 0.9){						return Color.yellow;					}				}			}			return MesquiteColorTable.getYellowScale(bestScore, 0, 2, false);		}	}	int center, otherCenter; //myLength, otherLength, 	public void paint(Graphics g){		//myLength = mySeq.getLength();		//otherLength = refSeq.getLength();		center = mySeqCanvas.getLocalIndexFromUniversalBase(parent.getCenterBase());		otherCenter = refSeqCanvas.getLocalIndexFromUniversalBase(otherPanel.getCenterBase());		mySequence = mySeq.getSequence();		otherSequence = refSeq.getSequence();		if (parent.isShownComplemented())			mySequence = DNAData.complementString(mySequence);		if (parent.isShownReversed()){			mySequence = DNAData.reverseString(mySequence);			center = mySequence.length()-center-1;		}		/**/		/**/		if (otherPanel.isShownComplemented())			otherSequence = DNAData.complementString(otherSequence);		if (otherPanel.isShownReversed()){			otherSequence = DNAData.reverseString(otherSequence);			otherCenter = otherSequence.length()-otherCenter-1;		}		mySequence = mySequence.toLowerCase();		otherSequence = otherSequence.toLowerCase();		//		COMPLEMENT IF NEEDED		//		REVERSE IF NEEDED AND then change center		g.translate(0, -14);		mySeqCanvas.paint(g, true, this);		g.translate(0, 14);		g.setColor(Color.black);		g.fillRect(0, getBounds().height-3, getBounds().width, 3);	}}class ContigControlPanel extends MesquitePanel  {	//close (all except for base)	//close individual reads	// reverse	// complement	// name	String name;	ContigPanel parent;	MesquiteButton reverseButton, complementButton, goAwayButton, alignButton, lockButton;	MesquiteButton[] chromatButtons=null;	boolean showChromatButtons = false;	boolean isPrimary;	public ContigControlPanel(String name, ContigPanel parent){		this.name = name;		this.parent = parent;		this.isPrimary = parent.isPrimary;		if (showChromatButtons) {			chromatButtons = new MesquiteButton[parent.getNumChromats()];			for (int i=0; i< parent.getNumChromats(); i++){				chromatButtons[i] = new MesquiteButton (parent.window.viewModule, MesquiteModule.makeCommand("toggleChromatVisible", parent), Integer.toString(i), true, parent.window.viewModule.getPath() + "triangleDownOn.gif", 12, 12);				chromatButtons[i].setOptionImagePath(parent.window.viewModule.getPath() + "triangleRightOn.gif");				add(chromatButtons[i]);				chromatButtons[i].setBounds(getBounds().width-20, parent.getChromatTop(i), 12, 12);				chromatButtons[i].setShowBackground(false);				chromatButtons[i].setVisible(true);				chromatButtons[i].setOptionDown(!parent.getChromatVisible(i));			}		}		int top = getBounds().height-36;		complementButton = new MesquiteButton (parent.window.viewModule, MesquiteModule.makeCommand("showComplemented", parent), null, true, parent.window.viewModule.getPath() + "complementOff.gif", 16, 16);		complementButton.setOptionImagePath(parent.window.viewModule.getPath() + "complementOn.gif");		add(complementButton);		complementButton.setBounds(4, top, 16, 16);		top -= 20;		complementButton.setShowBackground(false);		complementButton.setVisible(true);		complementButton.setOptionDown(parent.showComplemented.getValue());		reverseButton = new MesquiteButton (parent.window.viewModule, MesquiteModule.makeCommand("showReversed", parent), null, true, parent.window.viewModule.getPath() + "forward.gif", 16, 16);		reverseButton.setOptionImagePath(parent.window.viewModule.getPath() + "reverse.gif");		add(reverseButton);		reverseButton.setBounds(4, top, 16, 16);		top -= 20;		reverseButton.setShowBackground(false);		reverseButton.setVisible(true);		reverseButton.setOptionDown(parent.showReversed.getValue());		if (!isPrimary){			lockButton = new MesquiteButton (parent.window.viewModule, MesquiteModule.makeCommand("toggleLock", parent), null, true, parent.window.viewModule.getPath() + "unlocked.gif", 16, 16);			lockButton.setOptionImagePath(parent.window.viewModule.getPath() + "locked.gif");			add(lockButton);			lockButton.setBounds(4, top, 16, 16);			lockButton.setShowBackground(false);			lockButton.setVisible(true);			lockButton.setOptionDown(parent.locked);			top -= 20;						alignButton = new MesquiteButton (parent.window.viewModule, MesquiteModule.makeCommand("align", parent), null, true, parent.window.viewModule.getPath() + "alignToRef.gif", 16, 16);			add(alignButton);			alignButton.setBounds(4, top, 16, 16);			alignButton.setShowBackground(false);			alignButton.setVisible(true);			top -= 20;			goAwayButton = new MesquiteButton (parent.window.viewModule, MesquiteModule.makeCommand("goAway", parent), null, true, MesquiteModule.getRootImageDirectoryPath() + "goAway.gif", 16, 16);			add(goAwayButton);			goAwayButton.setBounds(4, top, 16, 16);			goAwayButton.setShowBackground(false);			goAwayButton.setVisible(true);		}	}	void resetButtons(){		if (showChromatButtons) {			for (int i=0; i< parent.getNumChromats(); i++){				chromatButtons[i].setOptionDown(!parent.getChromatVisible(i));				chromatButtons[i].repaint();			}		}	}	void resize(){		if (showChromatButtons) {			for (int i=0; i< parent.getNumChromats(); i++){				chromatButtons[i].setOptionDown(!parent.getChromatVisible(i));				chromatButtons[i].setBounds(getBounds().width-16, parent.getChromatTop(i) +2, 12, 12);				chromatButtons[i].repaint();			}		}		int top = getBounds().height-36;		complementButton.setBounds(4, top, 16, 16);		top -= 20;		reverseButton.setBounds(4, top, 16, 16);		top -= 20;		if (!isPrimary){			lockButton.setBounds(4, top, 16, 16);			top -= 20;			alignButton.setBounds(4, top, 16, 16);			top -= 20;			goAwayButton.setBounds(2, top, 16, 16);		}	}	public void setSize(int w, int h){		super.setSize( w,  h);		resize();	}	public void setBounds(int x, int y, int w, int h){		super.setBounds(x, y, w,  h);		resize();	}	public void setBackground(Color c){		super.setBackground(c);		if (goAwayButton != null)			goAwayButton.setBackground(c);		if (alignButton != null)			alignButton.setBackground(c);		if (lockButton != null)			lockButton.setBackground(c);		complementButton.setBackground(c);		reverseButton.setBackground(c);		if (showChromatButtons) {			for (int i=0; i< parent.getNumChromats(); i++){				chromatButtons[i].setBackground(c);			}		}	}	public void paint(Graphics g){		if (parent.isReversedInEditedData() || parent.isComplementedInEditedData())			setBackground(ColorDistribution.lightRed);		else			setBackground(ColorTheme.getContentBackground());		g.setColor(ColorTheme.getInterfaceEdgePositive());		g.drawRect(0,0,getBounds().width-1, getBounds().height);		//g.drawRect(1,1,getBounds().width-3, getBounds().height-3);	}}/* ======================================================================== */class ChromWindowPalette extends ToolPalette {	VChromWindow w;	MiniSlider magSlider, shadowSlider, shadowRedSlider;	int shadowWidth = 40;	public ChromWindowPalette(MesquiteModule ownerModule, VChromWindow containingWindow, MesquiteCommand sliderCommand, MesquiteCommand shadowOffsetCommand, MesquiteCommand shadowRedCommand) {  //in future pass general MesquiteWindow		super( ownerModule,  containingWindow, 1);		w = containingWindow;		magSlider = new MiniSlider (sliderCommand, false, 4, 0, 5, 0, 5);		add(magSlider);		magSlider.setVisible(true);		magSlider.setBounds(20, 100, 10, 200);		shadowSlider = new MiniSlider (shadowOffsetCommand, true, 2.5, 0, 5, 0, 5);		add(shadowSlider);		shadowSlider.se