romBoolean[window.FIXEDCHROMATOHEIGHT];		//		setNumBasesAddedToStart(ChromaseqUtil.getNumAddedToStart(data,taxon.getNumber(), true));		//		setNumBasesAddedToEnd(ChromaseqUtil.getNumAddedToEnd(data,taxon.getNumber(), true));		setBackground(ColorTheme.getContentBackground());		if (chroms != null){			closeupPanelContainer = window.getCloseupPanelContainer();			closeupPanel = new ChromatogramCloseupPanel(this,chromatograms[closeupChrom], this, closeupChrom, getID());			chromSetPanel = new MesquitePanel();			add(chromSetPanel);			chromPanels = new BasicChromatogramPanel[chroms.length];			//chromVis = new boolean[chroms.length];			for (int i=0; i<chroms.length; i++){				//chromVis[i] = true;				chromPanels[i] = new BasicChromatogramPanel(this,chroms[i], this, i, id);				chromPanels[i].setVisible(true);				chromPanels[i].setCloseupPanel(closeupPanel);				//	chromPanels[i].setBackground(Color.blue);				//addToWindow(chromPanels[i]);				chromSetPanel.add(chromPanels[i]);				totalPeakDistance+= chroms[i].getAveragePeakDistance();			}			peakDistance = totalPeakDistance/chroms.length;			}		originalUntrimmedPanel = new OriginalUntrimmedSeqPanel(this, contig, id);		originalUntrimmedPanel.setVisible(true);		originalUntrimmedPanel.setShowLowerQualSourceConflictsWithHigherQualRead(true);		add(originalUntrimmedPanel);		if (originalData != null){			originalSequence =new OrigSequenceImported(this,(MolecularData)data, (MolecularData)originalData, (ContinuousData)qualityData, contig, taxon.getNumber());			originalTrimmedPanel = new OriginalTrimmedSeqPanel(this, originalSequence, id);			originalTrimmedPanel.setVisible(true);			add(originalTrimmedPanel);		}		editedSequence =new EditedMatrixSequence(this,(MolecularData)data, (MolecularData)originalData, (ContinuousData)qualityData,  contig, taxon.getNumber());		matrixSeqPanel = new MatrixSequencePanel(this, editedSequence, id);		if (originalSequence == null)			originalSequence = editedSequence;		matrixSeqPanel.setVisible(true);		matrixSeqPanel.setBackground(Color.white);		add(matrixSeqPanel);				multiReadCallsPanel = new MultiReadCallsPanel(this,id,this, chroms);		multiReadCallsPanel.setVisible(true);		add(multiReadCallsPanel);		overviewPanel = new ContigOverviewPanel(this,id, this, chroms);		overviewPanel.setVisible(true);		add(overviewPanel);		bufferPanel = new MesquitePanel();	//	bufferPanel.setBackground(Color.green);		bufferPanel.setVisible(true);		add(bufferPanel);				int numPeaksVisible = getApproximateNumberOfPeaksVisible();		centerBase = numPeaksVisible/2;		horizScrollPanel = new MesquitePanel();		horizScrollBar = new Scrollbar(Scrollbar.HORIZONTAL);		horizScrollBar.addAdjustmentListener(this);		int scrollTotal = getTotalNumUniversalBases() + numPeaksVisible;		horizScrollBar.setValues(centerBase,numPeaksVisible,0,getTotalNumUniversalBases() + numPeaksVisible); 		horizScrollBar.setUnitIncrement(1);		horizScrollBar.setBlockIncrement(1);		//		hbar.addKeyListener(this);		horizScrollPanel.add("South",horizScrollBar);		//hbar.setVisible(true);		horizScrollPanel.setVisible(true);		horizScrollPanel.setBackground(Color.white);		add(horizScrollPanel);		vertScrollPanel = new MesquitePanel();		vertScrollBar = new Scrollbar(Scrollbar.VERTICAL);		vertScrollBar.addAdjustmentListener(this);		vertScrollBar.setValues(0,0,0,0); 		vertScrollBar.setUnitIncrement(1);		vertScrollBar.setBlockIncrement(1);		vertScrollPanel.add("EAST",vertScrollBar);		vertScrollPanel.setVisible(true);		vertScrollPanel.setBackground(Color.white);		add(vertScrollPanel);		/*		Associable tInfo = data.getTaxaInfo(false);		numBasesOriginallyTrimmedFromStartOfPhPhContig = 0;		if (tInfo != null && taxon != null) {			int it = taxon.getNumber();			long t =	ChromaseqUtil.getLongAssociated(tInfo,ChromaseqUtil.startTrimRef, it);			if (MesquiteLong.isCombinable(t)) {				numBasesOriginallyTrimmedFromStartOfPhPhContig = (int)t;				padBeforeTrim = contig.resetPadding(numBasesOriginallyTrimmedFromStartOfPhPhContig, false);				numBasesOriginallyTrimmedFromStartOfPhPhContig -= padBeforeTrim;			}		}		 */		synchChromToTable(true);		horizScrollBar.addKeyListener(this);		if (universalMapper==null){			universalMapper = new ChromaseqUniversalMapper(this, data);			universalMapper.reset(false);		}			}	public int getID(){		return id;	}	public int getTaxonNumber(){		return it;	}	public ChromaseqUniversalMapper getUniversalMapper() {		return universalMapper;	}//	public MesquiteWindow getWindow() {//		return window;//	}	public void setUniversalMapper(ChromaseqUniversalMapper universalMapper) {		this.universalMapper = universalMapper;	}	public void fullReregistration() {		ChromaseqUtil.inferRegistryData(data, it);		if (universalMapper!=null)			universalMapper.reset(false);	}	public void setUpPrimers(){		boolean atLeastOnePrimerMatch = false;		for (int i=0; i<chromPanels.length; i++){			if (chromPanels[i].setUpPrimers())				atLeastOnePrimerMatch=true;			if (!MesquiteThread.isScripting())				chromPanels[i].repaintPanel();		}		if (!atLeastOnePrimerMatch)			MesquiteMessage.println("No primers found to match sequences of the available reads.");	}	public void setReverseComplemented(boolean rc) {		boolean previousRev = isReversedInEditedData();		boolean previousComp = isComplementedInEditedData();		if (previousRev!=rc || previousComp!=rc) {			getEditedData().setReversed(getTaxon().getNumber(), rc);			getEditedData().setComplemented(getTaxon().getNumber(), rc);			repaintPanels();		}	}	public boolean isReversedInEditedData(){		return getEditedData().isReversed(getTaxon().getNumber());	}	public boolean isComplementedInEditedData(){		return getEditedData().isComplemented(getTaxon().getNumber());	}	public DNAData getEditedData() {		return data;	}	public BasicChromatogramPanel[] getChromPanels(){		return chromPanels;	}	public Chromatogram[] getChromatograms(){		return